import styles from './CardTask.module.css';
import { Link } from 'react-router-dom';
import edit from '../../assets/edit.svg';
import trash from '../../assets/trash.svg';


function CardTask({ task, deleteTask }) {
  return (
    <li className={styles.card}>
      <div className={styles.actions}>
        <span className={[styles.statusBadge, task.completed ? styles.done : styles.pending].join(' ')}>
          {task.completed ? 'Done' : 'Pending'}
        </span>
        <div className={styles.actions}>
          <Link to={`/edit/${task.id}`} className={styles.actionIcon}>
            <img src={edit} className={styles.editIcon}/>
          </Link>
          <button onClick={() => deleteTask(task.id)} className={styles.actionIcon}>
            <img src={trash} className={styles.deleteIcon}/>
          </button>
        </div>
      </div>
      <p className={styles.title}>
        {task.title}
      </p>
    </li>
  );
}

export default CardTask;
